mapper.sql:
SELECT T0.DS_ID AS DATA_ID, ( CASE WHEN T2.DATA_PERMISSION IS NULL THEN T1.DATA_PERMISSION WHEN T1.DATA_PERMISSION >= ${DP_MIN_READ_PERMISSION} THEN T1.DATA_PERMISSION ELSE T2.DATA_PERMISSION END ) AS DATA_PERMISSION FROM DATAGEAR_DATA_SET T0 LEFT JOIN (
) T1 ON T0.DS_ID = T1.DATA_ID LEFT JOIN (
) T2 ON T0.DS_AP_ID = T2.AP_ID
