mapper.insert:
INSERT INTO DATAGEAR_DATA_SET_JSON_VALUE
	(
	DS_ID, DS_VALUE, DS_DATA_JSON_PATH, DS_ADTND_PROPS
	)
VALUES
	(
	#{entity.id}, #{entity.value}, #{entity.dataJsonPath}, #{entity.additionDataProps}
	)

mapper.update:
