mapper.insert:
INSERT INTO DATAGEAR_SQL_HISTORY
	(
	SQLHIS_ID, SQLHIS_SQL, SQLHIS_SCHEMA_ID, SQLHIS_USER_ID
	)
VALUES
	(
	#{entity.id}, #{entity.sql}, #{entity.schemaId}, #{entity.userId}
	)

mapper.update:
UPDATE DATAGEAR_SQL_HISTORY SET
	SQLHIS_SQL = #{entity.sql},
	SQLHIS_SCHEMA_ID = #{entity.schemaId},
	SQLHIS_USER_ID = #{entity.userId}
WHERE
	SQLHIS_ID = #{entity.id}

mapper.delete:
DELETE FROM DATAGEAR_SQL_HISTORY WHERE SQLHIS_SCHEMA_ID = #{schemaId} AND SQLHIS_USER_ID = #{userId}
AND SQLHIS_ID NOT IN ( SELECT RHID.SQLHIS_ID FROM (
SELECT T.SQLHIS_ID FROM DATAGEAR_SQL_HISTORY T WHERE SQLHIS_SCHEMA_ID = #{schemaId} AND SQLHIS_USER_ID = #{userId} ORDER BY SQLHIS_CREATE_TIME DESC
) RHID )
AND SQLHIS_CREATE_TIME < #{deleteBeforeDate}
