choose.otherwise:
SELECT IDPQV.DATA_ID, MOD(MAX(DISTINCT IDPQV.AUTH_PRIORITY_PERMISSION), 100) AS DATA_PERMISSION FROM ( SELECT IDQV.DP_AUTH_DATA_ID AS DATA_ID, ( CASE
/*创建用户最高权限*/ WHEN IDQV.DP_AUTH_DATA_CREATOR_ID IS NOT NULL AND IDQV.DP_AUTH_DATA_CREATOR_ID = '${DP_CURRENT_USER.id}' THEN (9000 + ${DP_MAX_PERMISSION})
WHEN PQV.AUTH_RESOURCE_TYPE IS NULL THEN ${DP_UNSET_PERMISSION}0 /*模式匹配优先级低于具体资源优先级*/ WHEN PQV.AUTH_RESOURCE_TYPE = '${DP_RESOURCE_TYPE}_PATTERN' THEN (100 + PQV.AUTH_PRIORITY_PERMISSION) WHEN PQV.AUTH_RESOURCE_TYPE = '${DP_RESOURCE_TYPE}' THEN (200 + PQV.AUTH_PRIORITY_PERMISSION) END ) AS AUTH_PRIORITY_PERMISSION FROM (

		) IDQV
	LEFT JOIN
		(
			
		) PQV
	ON
		(PQV.AUTH_RESOURCE_TYPE = '${DP_RESOURCE_TYPE}' AND PQV.AUTH_RESOURCE = IDQV.DP_AUTH_DATA_ID)
		
		OR (PQV.AUTH_RESOURCE_TYPE = '${DP_RESOURCE_TYPE}_PATTERN' AND IDQV.DP_AUTH_DATA_PATTERN_SRC LIKE DATAGEAR_REPLACE(PQV.AUTH_RESOURCE, '*', '%'))
		
	) IDPQV
GROUP BY
	IDPQV.DATA_ID

mapper.sql:
SELECT DG_AUTH.AUTH_RESOURCE, DG_AUTH.AUTH_RESOURCE_TYPE, ( /*优先级加权至权限值，便于通过单个MAX取得优先级最高的那个权限值*/ CASE DG_AUTH.CREATOR_IS_ADMIN /*管理员授权始终高于普通用户*/ WHEN 'true' THEN CASE DG_AUTH.AUTH_PRINCIPAL_TYPE WHEN 'ALL' THEN (5000 + DG_AUTH.AUTH_PERMISSION) WHEN 'ANONYMOUS' THEN (6000 + DG_AUTH.AUTH_PERMISSION) WHEN 'ROLE' THEN (7000 + DG_AUTH.AUTH_PERMISSION) WHEN 'USER' THEN (8000 + DG_AUTH.AUTH_PERMISSION) ELSE 0 END ELSE CASE DG_AUTH.AUTH_PRINCIPAL_TYPE WHEN 'ALL' THEN (1000 + DG_AUTH.AUTH_PERMISSION) WHEN 'ANONYMOUS' THEN (2000 + DG_AUTH.AUTH_PERMISSION) WHEN 'ROLE' THEN (3000 + DG_AUTH.AUTH_PERMISSION) WHEN 'USER' THEN (4000 + DG_AUTH.AUTH_PERMISSION) ELSE 0 END END ) AS AUTH_PRIORITY_PERMISSION FROM ( SELECT A.*, U.USER_IS_ADMIN AS CREATOR_IS_ADMIN FROM DATAGEAR_AUTHORIZATION A, DATAGEAR_USER U WHERE A.AUTH_CREATE_USER_ID = U.USER_ID ) DG_AUTH WHERE DG_AUTH.AUTH_ENABLED = 'true' AND ( DG_AUTH.AUTH_RESOURCE_TYPE = '${DP_RESOURCE_TYPE}'
OR DG_AUTH.AUTH_RESOURCE_TYPE = '${DP_RESOURCE_TYPE}_PATTERN'
) AND ( DG_AUTH.AUTH_PRINCIPAL_TYPE = 'ALL'
OR DG_AUTH.AUTH_PRINCIPAL_TYPE = 'ANONYMOUS'
OR ( DG_AUTH.AUTH_PRINCIPAL_TYPE = 'ROLE' AND DG_AUTH.AUTH_PRINCIPAL IN ( SELECT RO.ROLE_ID FROM DATAGEAR_ROLE_USER RU INNER JOIN DATAGEAR_ROLE RO ON RU.RU_ROLE_ID = RO.ROLE_ID WHERE RU.RU_USER_ID = '${DP_CURRENT_USER.id}' AND RO.ROLE_ENABLED = 'true' ) ) OR (DG_AUTH.AUTH_PRINCIPAL_TYPE = 'USER' AND DG_AUTH.AUTH_PRINCIPAL = '${DP_CURRENT_USER.id}')
)
